  const [dropdown, setDropdown] = useState(false);
  const {register, handleSubmit }= useForm();
  var restaurants = [
    "Chipotle",
    "Qdoba",
    "Noodles and Company",
    "Ramen",
    "Maru Sushi",
    "Potbelly",
    "Jimmy Johns",
    "Einsteins"
  ];

