  return (
    <div className="App">
      <header className="header">
        <button className="button" onClick={() => {
          if (dropdown) {
            setDropdown(false);
          } else {
            setDropdown(true);
          }}}>
          Pick Random
        </button>
      </header>
      <Popup trigger={<button>All Food Options</button>} modal>
        <div>Chipotle</div>
        <div>Qdoba</div>
        <div>Noodles and Company</div>
        <div>Panda Express</div>
        <div>Jimmy Johns</div>
        <div>Potbellys</div>
        <div>Maru Sushi</div>
        <div>Einsteins</div>
        <div>Ramen Place</div>
      </Popup>

      {dropdown &&
        <form  onSubmit={handleSubmit(onSubmit)}>
          <label class="checkbox-inline" > 
          <input
            type="checkbox"
            onChange={handelInputChange}
            />Chipotle
          </label>


        </form>


      }

    </div>
  );
}

export default App;
