import './App.css';
import { useState, useEffect } from 'react';
import {useForm} from "react-hook-form";
import Popup from 'reactjs-popup';
import 'reactjs-popup/dist/index.css';

function App() {
  const [dropdown, setDropdown] = useState(false);
  const {register, handleSubmit }= useForm();
  const [edit, setEdit] = useState("Edit Restaurants")
  const [restaurants, editRestaurants] = [
    "Chipotle",
    "Qdoba",
    "Noodles and Company",
    "Ramen",
    "Maru Sushi",
    "Potbelly",
    "Jimmy Johns",
    "Einsteins"
  ];

  var randomItem = restaurants[Math.floor(Math.random()*restaurants.length)];

  const onSubmit = data => {
    console.log(data)
    console.log(randomItem)

  };

  const handelInputChange = event => {
    console.log(event)
    const target = event.target;
    const name = target.name;
    console.log(name)
  }


  useEffect(() => {

  },[]);

  return (
    <div className="App">
      <header className="header">
        <button className="button" onClick={() => {
          if (dropdown) {
            setDropdown(false);
            setEdit("Edit Restaurants")
          } else {
            setDropdown(true);
            setEdit("Stop Editing")
          }}}>
          {edit}
        </button>
      </header>
      <p></p>
      {dropdown &&
        <form  className="form" onSubmit={handleSubmit(onSubmit)}>
          <label> <input
            type="checkbox"
            name="Chipotle"
            onChange={handelInputChange}
            />Chipotle</label>
          <p></p>
          <label> <input
            type="checkbox"
            name="Qdoba"
            onChange={handelInputChange}
            />Qdoba</label> 
            <p></p>
          <label> <input
            type="checkbox"
            name="Noodles and Company"
            onChange={handelInputChange}
            />Noodles and Company</label> 
            <p></p>
          <label> <input
            type="checkbox"
            name="Panda Express"
            onChange={handelInputChange}
            />Panda Express</label>
            <p></p>
          <label> <input
            type="checkbox"
            onChange={handelInputChange}
            />Jimmy Johns</label>
            <p></p>
          <label> <input
            type="checkbox"
            onChange={handelInputChange}
            />Pot Bellys</label>
            <p></p>
          <label> <input
            type="checkbox"
            onChange={handelInputChange}
            />Maru Sushi</label>
            <p></p>
          <label> <input
            type="checkbox"
            onChange={handelInputChange}
            />Ramen Place</label>
            <p></p>

        </form>


      }

    </div>
  );
}

export default App;
