import './App.css';
import { useState, useEffect } from 'react';
import {useForm} from "react-hook-form";
import Popup from 'reactjs-popup';
import 'reactjs-popup/dist/index.css';

function App() {
  const [dropdown, setDropdown] = useState(false);
  const {register, handleSubmit }= useForm();
  var restaurants = [
    "Chipotle",
    "Qdoba",
    "Noodles and Company",
    "Ramen",
    "Maru Sushi",
    "Potbelly",
    "Jimmy Johns",
    "Einsteins"
  ];

  var randomItem = restaurants[Math.floor(Math.random()*restaurants.length)];

  const onSubmit = data => {
    console.log(data)
    console.log(randomItem)

  };

  const handelInputChange = event => {
    const target = event.target;
    const name = target.name;

  }


  useEffect(() => {

  },[]);

  return (
    <div className="App">
      <header className="header">
        <button className="button" onClick={() => {
          if (dropdown) {
            setDropdown(false);
          } else {
            setDropdown(true);
          }}}>
          Pick Random
        </button>
      </header>


      {dropdown &&
        <form  className="form" onSubmit={handleSubmit(onSubmit)}>
          <label> <input
            type="checkbox"
            onChange={handelInputChange}
            />Chipotle</label>
          <p></p>
          <label> <input
            type="checkbox"
            onChange={handelInputChange}
            />Qdoba</label> 
            <p></p>
          <label> <input
            type="checkbox"
            onChange={handelInputChange}
            />Noodles and Company</label> 
            <p></p>
          <label> <input
            type="checkbox"
            onChange={handelInputChange}
            />Panda Express</label>
            <p></p>
          <label> <input
            type="checkbox"
            onChange={handelInputChange}
            />Jimmy Johns</label>
            <p></p>
          <label> <input
            type="checkbox"
            onChange={handelInputChange}
            />Pot Bellys</label>
            <p></p>
          <label> <input
            type="checkbox"
            onChange={handelInputChange}
            />Maru Sushi</label>
            <p></p>
          <label> <input
            type="checkbox"
            onChange={handelInputChange}
            />Ramen Place</label>
            <p></p>

        </form>


      }

    </div>
  );
}

export default App;
